function getDeviceType(){return window.navigator.userAgent}function isPC(){let e=getDeviceType();return -1!=e.indexOf("Windows")||-1!=e.indexOf("Macintosh")||-1!=e.indexOf("Linux x86")}function isAndroid(){return -1!=getDeviceType().indexOf("Android")}function isIOS(){let e=getDeviceType();return -1!=e.indexOf("iPhone")||-1!=e.indexOf("iPad")}
